mapper.select:
SELECT c.USR_SEQ, c.USR_NM, c.RA_CD, c.GR_CD, c.ST_CD, d.SKILLS FROM ( SELECT a.PRJ_SEQ, b.USR_SEQ, b.USR_NM, b.RA_CD, b.GR_CD, b.ST_CD FROM ( SELECT * FROM USER_PROJECT_TABLE WHERE USR_SEQ = #{userProjectDTO.usrSeq} ) a RIGHT OUTER JOIN ( SELECT * FROM USER_INFO ) b ON a.USR_SEQ = b.USR_SEQ WHERE PRJ_SEQ IS NULL ) c RIGHT OUTER JOIN ( SELECT f.USR_SEQ, f.SKILLS FROM ( SELECT USR_SEQ , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS FROM ( SELECT * FROM USER_SKILL_TABLE WHERE SK_CD IN (1) ) GROUP BY USR_SEQ ) e
LEFT OUTER JOIN
( SELECT USR_SEQ , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS FROM USER_SKILL_TABLE GROUP BY USR_SEQ ) f ON e.USR_SEQ = f.USR_SEQ ) d ON c.USR_SEQ = d.USR_SEQ WHERE c.USR_SEQ IS NOT NULL
AND c.USR_SEQ = #{userProjectDTO.usrSeq}
AND c.USR_NM like '%' || #{userProjectDTO.usrNm} || '%'
AND c.RA_CD = #{userProjectDTO.raCD}
AND c.ST_CD = #{userProjectDTO.stCD}

SELECT COUNT(*) FROM ( SELECT c.USR_SEQ, c.USR_NM, c.RA_CD, c.GR_CD, c.ST_CD, d.SKILLS FROM ( SELECT a.PRJ_SEQ, b.USR_SEQ, b.USR_NM, b.RA_CD, b.GR_CD, b.ST_CD FROM ( SELECT * FROM USER_PROJECT_TABLE WHERE USR_SEQ = #{userProjectDTO.usrSeq} ) a RIGHT OUTER JOIN ( SELECT * FROM USER_INFO ) b ON a.USR_SEQ = b.USR_SEQ WHERE PRJ_SEQ IS NULL ) c RIGHT OUTER JOIN ( SELECT f.USR_SEQ, f.SKILLS FROM ( SELECT USR_SEQ , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS FROM ( SELECT * FROM USER_SKILL_TABLE WHERE SK_CD IN (1) ) GROUP BY USR_SEQ ) e
LEFT OUTER JOIN
( SELECT USR_SEQ , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS FROM USER_SKILL_TABLE GROUP BY USR_SEQ ) f ON e.USR_SEQ = f.USR_SEQ ) d ON c.USR_SEQ = d.USR_SEQ WHERE c.USR_SEQ IS NOT NULL
AND c.USR_SEQ = #{userProjectDTO.usrSeq}
AND c.USR_NM like '%' || #{userProjectDTO.usrNm} || '%'
AND c.RA_CD = #{userProjectDTO.raCD}
AND c.ST_CD = #{userProjectDTO.stCD}
)
