mapper.select:
    SELECT *
    FROM (
    SELECT ROWNUM as rnum, b.*
	FROM (
	SELECT a.USR_SEQ
	FROM USER_INFO a
	RIGHT OUTER JOIN (SELECT USR_SEQ
	, LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
	FROM (
	SELECT *
	FROM USER_SKILL_TABLE

		WHERE SK_CD IN  #{item} 

	)
	GROUP BY USR_SEQ) b
	ON a.USR_SEQ = b.USR_SEQ
	WHERE 1=1

		AND SKILLS = #{userDTO.skills}


		AND a.USR_SEQ = #{userDTO.usrSeq}


		AND a.USR_NM like '%' || #{userDTO.usrNm} || '%'


		AND a.GR_CD = #{userDTO.grCD}


		AND a.ST_CD = #{userDTO.stCD}


		AND a.USR_IN_DT BETWEEN #{userDTO.minDT} AND #{userDTO.maxDT}


		AND a.USR_IN_DT  <=  #{userDTO.maxDT}


		AND a.USR_IN_DT  >=  #{userDTO.minDT}

	) a

	LEFT OUTER JOIN 

	(
	SELECT a.*, b.SKILLS
	FROM USER_INFO a
	LEFT OUTER JOIN (SELECT USR_SEQ
	     , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
	FROM USER_SKILL_TABLE
	GROUP BY USR_SEQ
	) b
	ON a.USR_SEQ = b.USR_SEQ
	) b
	ON a.USR_SEQ = b.USR_SEQ
	ORDER BY a.USR_SEQ
	)
	WHERE RNUM >= #{startNum} and RNUM <= #{endNum}

    SELECT COUNT(*)
	FROM (
    SELECT *
	FROM (
	SELECT a.USR_SEQ
	FROM USER_INFO a
	RIGHT OUTER JOIN (SELECT USR_SEQ
	, LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
	FROM (
	SELECT *
	FROM USER_SKILL_TABLE

		WHERE SK_CD IN  #{item} 

	)
	GROUP BY USR_SEQ) b
	ON a.USR_SEQ = b.USR_SEQ
	WHERE 1=1

		AND SKILLS = #{userDTO.skills}


		AND a.USR_SEQ = #{userDTO.usrSeq}


		AND a.USR_NM like '%' || #{userDTO.usrNm} || '%'


		AND a.GR_CD = #{userDTO.grCD}


		AND a.ST_CD = #{userDTO.stCD}


		AND a.USR_IN_DT BETWEEN #{userDTO.minDT} AND #{userDTO.maxDT}


		AND a.USR_IN_DT  <=  #{userDTO.maxDT}


		AND a.USR_IN_DT  >=  #{userDTO.minDT}

	) a

	LEFT OUTER JOIN 

	(
	SELECT a.*, b.SKILLS
	FROM USER_INFO a
	LEFT OUTER JOIN (SELECT USR_SEQ
	     , LISTAGG(SK_CD, ',') WITHIN GROUP(ORDER BY SK_CD) AS SKILLS
	FROM USER_SKILL_TABLE
	GROUP BY USR_SEQ
	) b
	ON a.USR_SEQ = b.USR_SEQ
	) b
	ON a.USR_SEQ = b.USR_SEQ
	ORDER BY a.USR_SEQ
	)

mapper.update:
UPDATE USER_INFO SET USR_PW = #{usrPw}, USR_NM = #{usrNm}, USR_BDT = #{usrBDT}, GD_CD = #{gdCD}, USR_IN_DT = #{usrINDT}, ST_CD = #{stCD}, RA_CD = #{raCD}, GR_CD = #{grCD}, DV_CD = #{dvCD},
USR_IMG = #{usrImg},
USR_PN = #{usrPn}, USR_EM = #{usrEm}, USR_AD = #{usrAd} WHERE USR_SEQ = #{usrSeq}
